import React from "react";
import styled from "styled-components";
import Wassurf_white from "../../images/Wassurf_white.png"
import { Menu } from "@styled-icons/ionicons-solid/Menu"

const HeaderContainer = styled.div`
    width: 100%;
    background-color: #3FBED3;
    height: 100px;
    padding-left: 30px;
    padding-right: 30px;
    display: flex;
    position: fixed;
    align-items: center;
    .header-wrapper {
        align-items: center;
        border: solid red 1px;
    }
`
const LogoImage = styled.img`
    height: 50px;
    /* cursor: pointer; */
`

const MenuIcon = styled(Menu)`
    height: 30px;
    width: 30px;
    position: sticky;
    left: 2000px;
    margin-left: 10px;
    color: white;
    /* display: none; */
`
const MenuItems = styled.div`
    display: flex;
    position: sticky;
    margin-right: 240px;
    left: 1000px;
`

const MenuItem = styled.div`
    width: fit-content;
    margin-left: 40px;
    color: white;
`

const ButtonContainer = styled.div`
    width: fit-content;
    margin-left: 40px;
    position: sticky;
    left: 1340px;
    margin-right: 50px;

`

const LoginButton = styled.button`
    margin-right: 10px;
    height: 30px;
    width: 60px;
    background-color: transparent;
    border: white 2px solid;
    color: white;
    border-radius: 7px;
    :hover {
        border: black 2px solid;
        color: black;
        font-weight: bold;
    }
`

const SignupButton = styled.button`
    height: 30px;
    margin-right: 15px;
    height: 30px;
    width: 75px;
    background-color: transparent;
    border: white 2px solid;
    color: white;
    border-radius: 7px;
    :hover {
        border: black 2px solid;
        color: black;
        font-weight: bold;
    }
`

const Header = () => {
    return (
        <>
        <HeaderContainer>
            <LogoImage src={Wassurf_white} />
            <div className="header-wrapper">
            <MenuItems>
            <MenuItem>서핑스팟</MenuItem>
            <MenuItem>커뮤니티</MenuItem>
            <MenuItem>강습예약</MenuItem>
            </MenuItems>
            <ButtonContainer>
            <LoginButton>로그인</LoginButton>
            <SignupButton>회원가입</SignupButton>
            </ButtonContainer>
            <MenuIcon />
            </div>
        </HeaderContainer>
        </>
    )
}

export default Header;
